import React from 'react';

const LoginPopup = ({ onClose }) => {
  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center">
      <div className="bg-white p-8 rounded-md w-80">
        <h2 className="text-2xl mb-4">Login</h2>
        <form>
          <input type="text" placeholder="Username" className="w-full p-2 mb-4 border border-gray-300 rounded-md" />
          <input type="password" placeholder="Password" className="w-full p-2 mb-4 border border-gray-300 rounded-md" />
          <button type="submit" className="w-full bg-blue-600 text-white py-2 rounded-md">Login</button>
        </form>
        <button onClick={onClose} className="absolute top-2 right-2 text-gray-500">X</button>
      </div>
    </div>
  );
};

export default LoginPopup;
