import React, { useEffect, useState } from 'react';

const thStyle = {
  padding: '10px 8px',
  borderBottom: '1px solid #bbb',
  textAlign: 'left'
};
const tdStyle = {
  padding: '10px 8px',
  borderBottom: '1px solid #eee'
};

function parseDMY(dateStr) {
  if (!dateStr) return new Date(0);
  const [day, month, year] = dateStr.split('/');
  return new Date(`${year}-${month}-${day}`);
}

function formatDate(input) {
  if (!input) return '';
  const [year, month, day] = input.split('-');
  return `${day}/${month}/${year}`;
}

const InvoicePage = () => {
  const [invoices, setInvoices] = useState([]);
  const [loading, setLoading] = useState(true);
  const [fileSearch, setFileSearch] = useState('');
  const [dateFilter, setDateFilter] = useState('');

  useEffect(() => {
    fetch('http://localhost:8085/api/invoices')
      .then(res => res.json())
      .then(data => {
        setInvoices(Array.isArray(data) ? data : []);
        setLoading(false);
      });
  }, []);

  // Filter and sort
  const filteredInvoices = invoices
    .filter(item => {
      let match = true;
      if (fileSearch) {
        match = item.fileNumber && item.fileNumber.toLowerCase().includes(fileSearch.toLowerCase());
      }
      if (match && dateFilter) {
        match = item.invoice_date === formatDate(dateFilter);
      }
      return match;
    })
    .sort((a, b) => parseDMY(b.invoice_date) - parseDMY(a.invoice_date)) // oldest first
    .reverse(); // newest first

  return (
    <div>
      <form style={{ display: 'flex', gap: 12, margin: '16px 0' }}>
        <input
          style={{ flex: 1, padding: 10, borderRadius: 16, border: '1px solid #bbb' }}
          type="text"
          placeholder="Search by File Number"
          value={fileSearch}
          onChange={e => setFileSearch(e.target.value)}
          maxLength={50}
        />
        <input
          type="date"
          value={dateFilter}
          onChange={e => setDateFilter(e.target.value)}
        />
        <button
          type="button"
          className="w3-button w3-green"
          style={{
            flex: 1,
            borderRadius: 16,
            textAlign: 'center',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            color: '#fff',
            fontWeight: 'bold',
            border: 'none',
            background: '#43a047',
            fontSize: 18,
            cursor: 'pointer'
          }}
          // Add onClick for "New?" if needed
        >
          New?
        </button>
      </form>
      <div style={{
        background: '#43a047',
        color: '#fff',
        borderRadius: '8px 8px 0 0',
        padding: '12px 0',
        fontSize: 20,
        fontWeight: 600,
        textAlign: 'center',
        marginBottom: 0
      }}>
        Invoice
      </div>
      <table style={{
        width: '100%',
        borderCollapse: 'collapse',
        background: '#fff'
      }}>
        <thead>
          <tr style={{ background: '#ff9800', fontWeight: 600, color: '#fff' }}>
            <th style={thStyle}>ID</th>
            <th style={thStyle}>File Number</th>
            <th style={thStyle}>Invoice Date</th>
            <th style={thStyle}>Purpose</th>
            <th style={thStyle}>Total Price</th>
            <th style={thStyle}>Bank</th>
          </tr>
        </thead>
        <tbody>
          {loading ? (
            <tr>
              <td colSpan={6} style={{ textAlign: 'center', padding: 20 }}>Loading...</td>
            </tr>
          ) : filteredInvoices.length === 0 ? (
            <tr>
              <td colSpan={6} style={{ textAlign: 'center', padding: 20 }}>No records found.</td>
            </tr>
          ) : (
            filteredInvoices.map((item, idx) => (
              <tr key={item.invoice_id || idx} style={{ background: idx % 2 === 0 ? '#f9f9f9' : '#fff' }}>
                <td style={tdStyle}>{idx + 1}</td>
                <td style={tdStyle}>
                  <a href="#" style={{ color: '#2196f3', fontWeight: 600, textDecoration: 'underline' }}>
                    {item.fileNumber}
                  </a>
                </td>
                <td style={tdStyle}>{item.invoice_date}</td>
                <td style={tdStyle}>{item.valuation_purpose}</td>
                <td style={tdStyle}>{item.TOTAL_PRICE}</td>
                <td style={tdStyle}>{item.bank_name}</td>
              </tr>
            ))
          )}
        </tbody>
      </table>
    </div>
  );
};

export default InvoicePage;
