import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, useLocation, Navigate } from 'react-router-dom';
import Header from './components/Header';
import Banner from './components/Banner';
import About from './components/About';
import Empanelment from './components/BankLogos';
import Contact from './components/Contact';
import Footer from './components/Footer';
import Dashboard from './components/Dashboard';
import LoginPopup from './components/LoginPopup';
import { useAuth } from './components/AuthContext';

function ProtectedRoute({ children }) {
  const { isLoggedIn } = useAuth();
  return isLoggedIn ? children : <Navigate to="/" />;
}

function AppContent() {
  const { isLoggedIn, login } = useAuth();
  const location = useLocation();
  const [showLogin, setShowLogin] = useState(!isLoggedIn);

  // Show popup if not logged in and not on dashboard
  const shouldShowLoginPopup = !isLoggedIn && location.pathname !== '/dashboard';

  const handleLoginSuccess = () => {
    login();
    setShowLogin(false);
  };

  return (
    <div className="app-container" style={{ position: 'relative', minHeight: '100vh' }}>
      <Header />
      <main className="main-content">
        <Routes>
          <Route path="/" element={
            <>
              <section id="banner">
                <Banner />
              </section>
              <section id="about">
                <About />
              </section>
              <section id="empanelment">
                <Empanelment />
              </section>
              <section id="contact">
                <Contact />
              </section>
            </>
          } />
          <Route
            path="/dashboard"
            element={
              <ProtectedRoute>
                <Dashboard />
              </ProtectedRoute>
            }
          />
        </Routes>
        {shouldShowLoginPopup && (
          <div style={{
            position: 'fixed',
            top: 0, left: 0, right: 0, bottom: 0,
            background: 'rgba(0,0,0,0.3)',
            zIndex: 1000,
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center'
          }}>
            <LoginPopup onClose={() => setShowLogin(false)} onLoginSuccess={handleLoginSuccess} />
          </div>
        )}
      </main>
      <Footer />
    </div>
  );
}

function App() {
  return (
    <Router>
      <AppContent />
    </Router>
  );
}

export default App;
