import React, { useState } from 'react';
import '../styles/Banner.css';

const images = [
  "/images/slide1.jpg",
  "/images/slide2.jpg",
  "/images/slide3.jpg",
  "/images/slide4.jpg",
  // Add more if you want all 6:
  // "/images/slide5.jpg",
  // "/images/slide6.jpg",
];

const Banner = () => {
  const [current, setCurrent] = useState(0);

  const nextSlide = () => setCurrent((current + 1) % images.length);
  const prevSlide = () => setCurrent((current - 1 + images.length) % images.length);

  return (
    <div className="banner">
      <button className="banner-slider-btn" onClick={prevSlide}>&lt;</button>
      <img src={images[current]} alt={`Banner Slide ${current + 1}`} className="banner-image" />
      <button className="banner-slider-btn" onClick={nextSlide}>&gt;</button>
      <h2 className="banner-text">KM Valuers</h2>
    </div>
  );
};

export default Banner;
