import React, { useState } from "react";
import "../styles/ImageSlider.css"; // Updated path

const images = [
  "/images/slide1.jpg",
  "/images/slide2.jpg",
  "/images/slide3.jpg",
  "/images/slide4.jpg",
  "/images/slide5.jpg",
  "/images/slide6.jpg",
];

const ImageSlider = () => {
  const [current, setCurrent] = useState(0);

  const nextSlide = () => setCurrent((current + 1) % images.length);
  const prevSlide = () => setCurrent((current - 1 + images.length) % images.length);

  return (
    <div className="slider">
      <button className="slider-btn" onClick={prevSlide}>&lt;</button>
      <img src={images[current]} alt={`slide ${current + 1}`} className="slider-img" />
      <button className="slider-btn" onClick={nextSlide}>&gt;</button>
    </div>
  );
};

export default ImageSlider;
