import React, { useState, useEffect } from 'react';
import '../styles/Banner.css';

const images = [
  "/images/slide1.jpg",
  "/images/slide2.jpg",
  "/images/slide3.jpg",
  "/images/slide4.jpg",
  "/images/slide5.jpg",
  "/images/slide6.jpg",
];

const Banner = () => {
  const [current, setCurrent] = useState(0);

  const nextSlide = () => setCurrent((prev) => (prev + 1) % images.length);
  const prevSlide = () => setCurrent((prev) => (prev - 1 + images.length) % images.length);

  // Auto-slide every 5 seconds
  useEffect(() => {
    const interval = setInterval(nextSlide, 5000);
    return () => clearInterval(interval);
  }, [current]);

  return (
    <div className="banner">
      {images.map((img, index) => (
        <img
          key={index}
          src={img}
          alt={`Slide ${index + 1}`}
          className={`banner-image ${index === current ? "active" : ""}`}
        />
      ))}
      <button className="banner-slider-btn" onClick={prevSlide}>&lt;</button>
      <button className="banner-slider-btn" onClick={nextSlide}>&gt;</button>
      <h2 className="banner-text">KM Valuers</h2>
    </div>
  );
};

export default Banner;
