import React from 'react';

const LoginPopup = ({ onClose }) => {
  return (
    <div className="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center z-50">
      <div className="bg-white p-6 rounded-xl shadow-lg w-80 relative">
        <button onClick={onClose} className="absolute top-2 right-3 text-xl">&times;</button>
        <h2 className="text-lg font-bold mb-4">Login</h2>
        <form>
          <input type="text" placeholder="Username" className="w-full mb-3 p-2 border rounded" />
          <input type="password" placeholder="Password" className="w-full mb-4 p-2 border rounded" />
          <button type="submit" className="w-full bg-blue-600 text-white py-2 rounded">Login</button>
        </form>
      </div>
    </div>
  );
};

export default LoginPopup;
