import React, { useState } from 'react';
import LoginPopup from './LoginPopup';
import './Header.css'; // Assuming you have a CSS file for styling

const Header = () => {
  const [showLogin, setShowLogin] = useState(false);

  const handleLoginClick = () => setShowLogin(true);
  const handleClose = () => setShowLogin(false);

  const scrollToSection = (id) => {
    document.getElementById(id)?.scrollIntoView({ behavior: 'smooth' });
  };

  return (
    <>
      <header className="fixed top-0 left-0 w-full bg-white shadow-md z-50 flex justify-between items-center px-6 py-4">
        <h1 className="text-xl font-bold text-gray-800">KM Valuers</h1>
        <nav className="space-x-6">
          <button onClick={() => scrollToSection('empanelment')} className="hover:text-blue-600">Empanelment</button>
          <button onClick={() => scrollToSection('about')} className="hover:text-blue-600">About</button>
          <button onClick={() => scrollToSection('contact')} className="hover:text-blue-600">Contact</button>
          <button onClick={handleLoginClick} className="hover:text-blue-600">Login</button>
        </nav>
      </header>
      {showLogin && <LoginPopup onClose={handleClose} />}
    </>
  );
};

export default Header;
