import React, { useState } from 'react';

const InwardRegisterForm = () => {
  const [form, setForm] = useState({
    fileNumber: '',
    // ...add all other fields as per your form
  });

  const handleChange = e => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = e => {
    e.preventDefault();
    fetch('http://localhost:8085/api/inward_register', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(form)
    })
      .then(res => res.json())
      .then(() => {
        // Optionally redirect back to registry page or show a success message
      });
  };

  return (
    <form onSubmit={handleSubmit}>
      <input name="fileNumber" value={form.fileNumber} onChange={handleChange} placeholder="File Number" required />
      {/* Add all other fields here */}
      <button type="submit">Register</button>
    </form>
  );
};

export default InwardRegisterForm;
